// Images
declare module '*.svg' {
  const content: any
  export default content
}

declare module '*.png' {
  const content: any
  export default content
}

declare module '*.jpg' {
  const content: any
  export default content
}

declare module '*.jpeg' {
  const content: any
  export default content
}

declare module '*.webp' {
  const content: any
  export default content
}

// YAML data
declare module '*.yaml' {
  const data: any
  export default data
}
